#'
#' @export
gsw_sp_from_c <- function(c, t, p) {
  recycled <- recycle_common(c, t, p)
  .Call(teos10_c_gsw_sp_from_c_wrap, recycled[[1]], recycled[[2]], recycled[[3]])
}

